/* Study Dashboard - CASCADE Inspired Styles */

.study-dashboard {
  padding: 24px;
  background: linear-gradient(135deg, #0f1419 0%, #1a1f2e 50%, #2d1b3d 100%);
  min-height: 100vh;
  color: #e9edf1;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  overflow-y: auto;
}

/* Header Section */
.dashboard-header {
  margin-bottom: 32px;
}

.welcome-section {
  text-align: center;
  margin-bottom: 28px;
}

.welcome-title {
  font-size: 2.5rem;
  font-weight: 700;
  margin: 0 0 8px 0;
  background: linear-gradient(135deg, #4caf50, #66bb6a, #81c784);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.welcome-subtitle {
  font-size: 1.1rem;
  color: #a0aec0;
  margin: 0;
}

/* Stats Overview */
.stats-overview {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
  gap: 20px;
}

.stat-card {
  background: rgba(255, 255, 255, 0.08);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.12);
  border-radius: 20px;
  padding: 24px;
  display: flex;
  align-items: center;
  gap: 16px;
  transition: all 0.3s ease;
  cursor: pointer;
}

.stat-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 12px 40px rgba(0, 0, 0, 0.4);
  background: rgba(255, 255, 255, 0.12);
}

.stat-card.streak:hover {
  border-color: #ffa726;
  box-shadow: 0 12px 40px rgba(255, 167, 38, 0.3);
}

.stat-card.problems:hover {
  border-color: #4caf50;
  box-shadow: 0 12px 40px rgba(76, 175, 80, 0.3);
}

.stat-card.time:hover {
  border-color: #2196f3;
  box-shadow: 0 12px 40px rgba(33, 150, 243, 0.3);
}

.stat-icon {
  font-size: 2rem;
  opacity: 0.9;
}

.stat-content {
  display: flex;
  flex-direction: column;
}

.stat-number {
  font-size: 1.8rem;
  font-weight: 700;
  color: #ffffff;
  line-height: 1;
}

.stat-label {
  font-size: 0.9rem;
  color: #a0aec0;
  margin-top: 4px;
}

/* Section Titles */
.section-title {
  font-size: 1.4rem;
  font-weight: 600;
  margin: 0 0 20px 0;
  color: #ffffff;
}

/* Daily Progress Section */
.daily-progress-section {
  margin-bottom: 40px;
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 24px;
  padding: 32px;
}

.progress-container {
  display: flex;
  align-items: center;
  gap: 40px;
  flex-wrap: wrap;
}

.circular-progress {
  position: relative;
  flex-shrink: 0;
}

.progress-ring {
  transform: rotate(-90deg);
}

.progress-ring-fill {
  transition: stroke-dasharray 0.6s ease;
}

.progress-center {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
}

.progress-percentage {
  display: block;
  font-size: 1.5rem;
  font-weight: 700;
  color: #4caf50;
}

.progress-label {
  display: block;
  font-size: 0.8rem;
  color: #a0aec0;
  margin-top: 4px;
}

.progress-details {
  flex: 1;
  min-width: 250px;
}

.progress-item {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 16px;
  padding: 12px 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.08);
}

.progress-item:last-child {
  border-bottom: none;
}

.progress-icon {
  font-size: 1.2rem;
  width: 24px;
  text-align: center;
}

.progress-text {
  color: #e9edf1;
  font-size: 0.95rem;
}

/* Study Plan Cards */
.study-plans-section {
  margin-bottom: 40px;
}

.study-plan-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 24px;
}

.plan-card {
  background: rgba(255, 255, 255, 0.08);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.12);
  border-radius: 20px;
  padding: 24px;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
}

.plan-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 4px;
  background: linear-gradient(90deg, transparent, rgba(76, 175, 80, 0.5), transparent);
  opacity: 0;
  transition: opacity 0.3s ease;
}

.plan-card:hover {
  transform: translateY(-6px);
  box-shadow: 0 16px 48px rgba(0, 0, 0, 0.4);
  border-color: rgba(255, 255, 255, 0.2);
}

.plan-card:hover::before {
  opacity: 1;
}

.plan-card.active {
  border-color: #4caf50;
  background: rgba(76, 175, 80, 0.08);
  box-shadow: 0 8px 32px rgba(76, 175, 80, 0.2);
}

.plan-card.active::before {
  background: linear-gradient(90deg, #4caf50, #66bb6a, #4caf50);
  opacity: 1;
}

.card-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 16px;
}

.card-title-section {
  flex: 1;
}

.card-title {
  font-size: 1.2rem;
  font-weight: 600;
  margin: 0 0 8px 0;
  color: #ffffff;
}

.difficulty-badge {
  display: inline-block;
  padding: 4px 12px;
  border-radius: 16px;
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.difficulty-badge.easy {
  background: rgba(76, 175, 80, 0.2);
  color: #4caf50;
  border: 1px solid rgba(76, 175, 80, 0.3);
}

.difficulty-badge.medium {
  background: rgba(255, 167, 38, 0.2);
  color: #ffa726;
  border: 1px solid rgba(255, 167, 38, 0.3);
}

.difficulty-badge.hard {
  background: rgba(244, 67, 54, 0.2);
  color: #f44336;
  border: 1px solid rgba(244, 67, 54, 0.3);
}

.active-indicator {
  font-size: 0.8rem;
  color: #4caf50;
  font-weight: 500;
  padding: 4px 8px;
  background: rgba(76, 175, 80, 0.1);
  border-radius: 8px;
  border: 1px solid rgba(76, 175, 80, 0.2);
}

.card-description {
  color: #b0bec5;
  margin: 0 0 16px 0;
  line-height: 1.5;
}

.topics-list {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-bottom: 20px;
}

.topic-tag {
  background: rgba(255, 255, 255, 0.1);
  color: #e0e6ed;
  padding: 4px 10px;
  border-radius: 12px;
  font-size: 0.8rem;
  border: 1px solid rgba(255, 255, 255, 0.15);
}

.progress-section {
  margin-bottom: 20px;
}

.progress-bar {
  width: 100%;
  height: 8px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 4px;
  overflow: hidden;
  margin-bottom: 8px;
}

.progress-fill {
  height: 100%;
  background: linear-gradient(90deg, #4caf50, #66bb6a);
  border-radius: 4px;
  transition: width 0.6s ease;
}

.progress-info {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.progress-text {
  color: #4caf50;
  font-weight: 500;
  font-size: 0.9rem;
}

.time-estimate {
  color: #78909c;
  font-size: 0.8rem;
}

.card-actions {
  text-align: center;
}

.start-session-btn {
  width: 100%;
  padding: 14px 24px;
  border: none;
  border-radius: 12px;
  font-weight: 600;
  font-size: 0.95rem;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
}

.start-session-btn.active {
  background: linear-gradient(135deg, #4caf50, #66bb6a);
  color: white;
  box-shadow: 0 4px 16px rgba(76, 175, 80, 0.4);
}

.start-session-btn.active:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 24px rgba(76, 175, 80, 0.5);
}

.start-session-btn.continue {
  background: linear-gradient(135deg, #2196f3, #42a5f5);
  color: white;
  box-shadow: 0 4px 16px rgba(33, 150, 243, 0.4);
}

.start-session-btn.continue:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 24px rgba(33, 150, 243, 0.5);
}

.start-session-btn.locked {
  background: rgba(255, 255, 255, 0.1);
  color: #78909c;
  cursor: not-allowed;
}

/* Quick Actions */
.quick-actions-section {
  margin-bottom: 40px;
}

.action-buttons-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 16px;
}

.action-btn {
  display: flex;
  align-items: center;
  gap: 16px;
  padding: 20px;
  background: rgba(255, 255, 255, 0.06);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 16px;
  color: #e9edf1;
  cursor: pointer;
  transition: all 0.3s ease;
  text-align: left;
}

.action-btn:hover {
  transform: translateY(-3px);
  box-shadow: 0 12px 32px rgba(0, 0, 0, 0.3);
  background: rgba(255, 255, 255, 0.1);
  border-color: rgba(255, 255, 255, 0.2);
}

.btn-icon {
  font-size: 1.8rem;
  flex-shrink: 0;
}

.btn-content {
  flex: 1;
}

.btn-title {
  display: block;
  font-weight: 600;
  font-size: 1rem;
  color: #ffffff;
  margin-bottom: 4px;
}

.btn-subtitle {
  display: block;
  font-size: 0.85rem;
  color: #a0aec0;
  line-height: 1.3;
}

/* Recent Activity */
.recent-activity-section {
  margin-bottom: 24px;
}

.activity-feed {
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  padding: 24px;
}

.activity-item {
  display: flex;
  align-items: flex-start;
  gap: 16px;
  padding: 16px 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.08);
  transition: all 0.2s ease;
}

.activity-item:last-child {
  border-bottom: none;
}

.activity-item:hover {
  background: rgba(255, 255, 255, 0.05);
  border-radius: 12px;
  margin: 0 -12px;
  padding: 16px 12px;
}

.activity-icon {
  font-size: 1.5rem;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  flex-shrink: 0;
}

.activity-content {
  flex: 1;
}

.activity-main {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 4px;
}

.activity-title {
  font-weight: 600;
  color: #ffffff;
  font-size: 0.95rem;
}

.activity-time {
  color: #78909c;
  font-size: 0.8rem;
}

.activity-subtitle {
  color: #b0bec5;
  font-size: 0.85rem;
  margin: 0;
  line-height: 1.4;
}

/* Activity Type Colors */
.activity-item.success .activity-icon {
  background: rgba(76, 175, 80, 0.2);
  color: #4caf50;
}

.activity-item.practice .activity-icon {
  background: rgba(33, 150, 243, 0.2);
  color: #2196f3;
}

.activity-item.streak .activity-icon {
  background: rgba(255, 167, 38, 0.2);
  color: #ffa726;
}

.activity-item.achievement .activity-icon {
  background: rgba(156, 39, 176, 0.2);
  color: #9c27b0;
}

/* Responsive Design */
@media (max-width: 768px) {
  .study-dashboard {
    padding: 16px;
  }

  .welcome-title {
    font-size: 2rem;
  }

  .stats-overview {
    grid-template-columns: 1fr;
  }

  .progress-container {
    flex-direction: column;
    text-align: center;
  }

  .study-plan-grid {
    grid-template-columns: 1fr;
  }

  .action-buttons-grid {
    grid-template-columns: 1fr;
  }

  .activity-main {
    flex-direction: column;
    align-items: flex-start;
    gap: 4px;
  }
}

@media (max-width: 480px) {
  .progress-details {
    min-width: auto;
  }

  .stat-card {
    padding: 16px;
  }

  .plan-card {
    padding: 20px;
  }
}

/* Smooth Animations */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.study-dashboard > * {
  animation: fadeInUp 0.6s ease-out;
}

.study-dashboard > *:nth-child(2) {
  animation-delay: 0.1s;
}

.study-dashboard > *:nth-child(3) {
  animation-delay: 0.2s;
}

.study-dashboard > *:nth-child(4) {
  animation-delay: 0.3s;
}

.study-dashboard > *:nth-child(5) {
  animation-delay: 0.4s;
}

import React from 'react';
import StudyDashboard from '../../components/StudyDashboard';

const Study = () => {
  return (
    <div className="study-panel">
      <StudyDashboard />
    </div>
  );
};

export default Study;
